'use strict';

angular.module('oitozero.ngSweetAlert', [])
    .factory('SweetAlert', ['$rootScope', '$q', function ($rootScope, $q) {

        var swal = window.swal;

        //public methods
        return {

            swal: function (arg1, arg2, arg3) {
                $rootScope.$evalAsync(function () {
                    if (typeof(arg2) === 'function') {
                        swal(arg1, function (isConfirm) {
                            $rootScope.$evalAsync(function () {
                                arg2(isConfirm);
                            });
                        }, arg3);
                    } else {
                        swal(arg1, arg2, arg3);
                    }
                });
            },
            success: function (title, message) {
                $rootScope.$evalAsync(function () {
                    swal(title, message, 'success');
                });
            },
            error: function (title, message) {
                $rootScope.$evalAsync(function () {
                    swal(title, message, 'error');
                });
            },
            warning: function (title, message) {
                $rootScope.$evalAsync(function () {
                    swal(title, message, 'warning');
                });
            },
            info: function (title, message) {
                $rootScope.$evalAsync(function () {
                    swal(title, message, 'info');
                });
            },
            confirm: function (title, text, fn, data) {
                $rootScope.$evalAsync(function () {
                    swal({
                        title: title,
                        text: text,
                        type: "warning",
                        showCancelButton: true,
                        confirmButtonColor: "#DD6B55",
                        confirmButtonText: "Yes",
                        cancelButtonText: "No",
                        closeOnConfirm: false,
                        closeOnCancel: false
                    }, function (isConfirm) {
                        if (isConfirm) {
                            return fn(data).then(function () {
                                swal("Deleted!", "Your todo has been deleted", "success");
                            });
                        } else {
                            swal("Cancelled", "Your todo was not deleted", "error");
                        }
                    });
                });
            }
        };
    }]);
